package smartTechSolution.b34;

public class Else_IfCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
             int gradeNumber =0;
             
             if(gradeNumber==80) {
            	 System.out.println("student get grade 'A'");
            	 
             }
    
             else if(gradeNumber  > 80  && gradeNumber < 90) {
            	 System.out.println("student get grade 'A'");
   	 
             }
             
             else if(gradeNumber==90) {
            	 System.out.println("student get grade 'A+'");
            	 
             }
             else if(gradeNumber>=90 && gradeNumber<=100 ) {
            	 System.out.println("student get grade 'A+'");
            	 
             }
             
             else if(gradeNumber==70) {
            	 System.out.println("student get grade 'A-'");
            	
            	 
             }
             else if(gradeNumber  > 70  && gradeNumber < 80) {
            	 System.out.println("student get grade 'A-'");
   	 
             }
             else if(gradeNumber==60) {
            	 System.out.println("student get grade 'B'");
            	 
             }
             else if(gradeNumber  > 60  && gradeNumber < 70) {
            	 System.out.println("student get grade 'B'");
   	 
             }
             else if(gradeNumber==55) {
            	 System.out.println("student get grade 'B-'");
            	 
             }
             else if(gradeNumber  > 55  && gradeNumber < 60) {
            	 System.out.println("student get grade 'B-'");
   	 
             }
             else if(gradeNumber==50) {
            	 System.out.println("student get grade 'C'");
            	 
             }  else if(gradeNumber  >= 50  && gradeNumber < 55) {
            	 System.out.println("student get grade 'C', which is poor");
   	 
             }
             else {
            	 System.out.println("Studen have to give the exam again");
             }
             
             
             
             
             
             
             
             
             
	}

}
